import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList, TextInput, Button, Alert } from 'react-native';
import axios from 'axios';

const ManageDriverScreen = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [question, setQuestion] = useState('');
  const [option1, setOption1] = useState('');
  const [option2, setOption2] = useState('');
  const [option3, setOption3] = useState('');
  const [option4, setOption4] = useState('');
  const [correctAnswer, setCorrectAnswer] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchQuizzes();
  }, []);

  const fetchQuizzes = async () => {
    try {
      const response = await axios.get('http://localhost:3011/quizzes');
      setQuizzes(response.data);
    } catch (error) {
      console.error('Error fetching quizzes:', error);
      Alert.alert('Error', 'Failed to fetch quizzes. Please try again.');
    }
  };

  const handleAddQuestion = async () => {
    if (!selectedQuiz || !question || !option1 || !option2 || !option3 || !option4 || !correctAnswer) {
      Alert.alert('Error', 'Please fill in all fields.');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(`http://localhost:3011/quizzes/${selectedQuiz.id}/questions`, {
        question_text: question,
        answers: [
          { answer_text: option1, is_correct: correctAnswer === 'A' },
          { answer_text: option2, is_correct: correctAnswer === 'B' },
          { answer_text: option3, is_correct: correctAnswer === 'C' },
          { answer_text: option4, is_correct: correctAnswer === 'D' },
        ],
      });

      if (response.status === 201) {
        Alert.alert('Success', 'Question added successfully.');
        // Clear input fields after successful addition
        setQuestion('');
        setOption1('');
        setOption2('');
        setOption3('');
        setOption4('');
        setCorrectAnswer('');
      } else {
        Alert.alert('Error', 'Failed to add question. Please try again.');
      }
    } catch (error) {
      console.error('Error adding question:', error);
      Alert.alert('Error', 'Failed to add question. Please try again.');
    }

    setLoading(false);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Select Quiz:</Text>
      <FlatList
        data={quizzes}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={[
              styles.quizItem,
              selectedQuiz && selectedQuiz.id === item.id && styles.selectedQuizItem,
            ]}
            onPress={() => setSelectedQuiz(item)}
          >
            <Text>{item.title}</Text>
          </TouchableOpacity>
        )}
      />
      {selectedQuiz && (
        <View style={styles.form}>
          <Text style={styles.title}>Add Question:</Text>
          <TextInput
            style={styles.input}
            placeholder="Question"
            value={question}
            onChangeText={setQuestion}
          />
          <TextInput
            style={styles.input}
            placeholder="Option 1"
            value={option1}
            onChangeText={setOption1}
          />
          <TextInput
            style={styles.input}
            placeholder="Option 2"
            value={option2}
            onChangeText={setOption2}
          />
          <TextInput
            style={styles.input}
            placeholder="Option 3"
            value={option3}
            onChangeText={setOption3}
          />
          <TextInput
            style={styles.input}
            placeholder="Option 4"
            value={option4}
            onChangeText={setOption4}
          />
          <TextInput
            style={styles.input}
            placeholder="Correct Answer (e.g., A, B, C, D)"
            value={correctAnswer}
            onChangeText={setCorrectAnswer}
          />
          <Button
            title="Add Question"
            onPress={handleAddQuestion}
            disabled={loading}
          />
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  quizItem: {
    padding: 10,
    backgroundColor: '#f0f0f0',
    marginBottom: 10,
  },
  selectedQuizItem: {
    backgroundColor: '#d0d0d0',
  },
  form: {
    marginTop: 20,
  },
  input: {
    height: 40,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 5,
    marginBottom: 10,
    paddingLeft: 10,
  },
});

export default ManageDriverScreen;
