import React, { useState } from 'react';
import { View, Text, Button, Alert } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const ApplicationLicenseScreen = () => {
  const navigation = useNavigation();
  const [nationalId, setNationalId] = useState('');

  const navigateToQuizScreen = () => {
    navigation.navigate('QuizScreen');
  };

  const navigateToApplication = () => {
    navigation.navigate('ApplicationForm');
  };

  const handleDrivingTest = async () => {
    try {
      const enteredNationalId = await promptForNationalId();
      if (enteredNationalId) {
        const score = await fetchScore(enteredNationalId);
        if (score !== null) {
          Alert.alert('Score', `Your driving test score is: ${score}`);
        } else {
          Alert.alert('Error', 'Failed to fetch driving test score.');
        }
      }
    } catch (error) {
      console.error('Error:', error);
      Alert.alert('Error', 'An error occurred. Please try again later.');
    }
  };

  const promptForNationalId = () => {
    return new Promise((resolve) => {
      Alert.prompt('Enter National ID', 'Please enter your 12-digit National ID:', [
        {
          text: 'Cancel',
          onPress: () => resolve(null),
          style: 'cancel',
        },
        {
          text: 'Submit',
          onPress: (nationalId) => resolve(nationalId),
        },
      ]);
    });
  };

  const fetchScore = async (nationalId) => {
    try {
      const response = await fetch(`http://localhost:8080/practicalscore?applicant_national_id=${nationalId}`);
      if (response.ok) {
        const data = await response.json();
        if (data && data.length > 0) {
          return data[0].score; // Assuming only one score entry per applicant
        } else {
          return null;
        }
      } else {
        return null;
      }
    } catch (error) {
      console.error('Error fetching score:', error);
      return null;
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>DRIVING LICENCE</Text>
      <View style={styles.buttonContainer}>
        <Button title="Apply for Drivers License" onPress={navigateToApplication} />
        <Button title="Take Quiz" onPress={navigateToQuizScreen} />
        <Button title="Driving Test" onPress={handleDrivingTest} />
      </View>
    </View>
  );
};

const styles = {
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  buttonContainer: {
    marginTop: 10,
    width: '100%',
  },
};

export default ApplicationLicenseScreen;
