import React, { useState } from 'react';
import { View, Text, Button, TextInput, Modal, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const ApplicationLicenseScreen = () => {
  const navigation = useNavigation();
  const [nationalId, setNationalId] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  const navigateToQuizScreen = () => {
    navigation.navigate('QuizScreen');
  };

  const navigateToApplication = () => {
    navigation.navigate('ApplicationForm');
  };

  const handleDrivingTest = async () => {
    setModalVisible(true);
  };

  const handleModalSubmit = () => {
    setModalVisible(false);
    // Here, you can perform any action with the entered national ID
    console.log('National ID:', nationalId);
    // Example: Fetch score using national ID
    // fetchScore(nationalId);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>DRIVING LICENCE</Text>
      <View style={styles.buttonContainer}>
        <Button title="Apply for Drivers License" onPress={navigateToApplication} />
        <Button title="Take Quiz" onPress={navigateToQuizScreen} />
        <Button title="Driving Test" onPress={handleDrivingTest} />
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(false);
        }}>
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Enter National ID:</Text>
            <TextInput
              style={styles.input}
              onChangeText={setNationalId}
              value={nationalId}
              keyboardType="numeric"
            />
            <View style={styles.buttonRow}>
              <Button title="Cancel" onPress={() => setModalVisible(false)} />
              <Button title="Submit" onPress={handleModalSubmit} />
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  buttonContainer: {
    marginTop: 10,
    width: '100%',
  },
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalView: {
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    elevation: 5,
  },
  modalText: {
    fontSize: 18,
    marginBottom: 20,
  },
  input: {
    height: 40,
    width: '100%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    paddingHorizontal: 10,
  },
  buttonRow: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    width: '100%',
  },
});

export default ApplicationLicenseScreen;
